import java.util.Stack;
public class Solution {
    public ListNode ReverseList(ListNode head) {
        if(head == null){
            return head;
        }
        ListNode pre = head;
        ListNode curr = head.next;
        ListNode tmp = null;

        while(curr != null){
            tmp = curr.next;
            curr.next = pre;
            pre = curr;
            curr = tmp;
        }
        head.next = null;
        head = pre;
        return head;
    }
}

