import java.util.ArrayList;
import java.util.Stack;
/*
public class TreeNode {
    int val = 0;
    TreeNode left = null;
    TreeNode right = null;

    public TreeNode(int val) {
        this.val = val;

    }

}
*/
public class Solution {
 public ArrayList> Print(TreeNode pRoot) {
        int depth = 1;
        Stack s1 = new Stack<>();
        s1.push(pRoot);
        Stack s2 = new Stack<>();
        ArrayList> lists = new ArrayList<>();

        while(!s1.isEmpty() || !s2.isEmpty()){
            if(depth%2 != 0){
                ArrayList temp = new ArrayList<>();
                while(!s1.isEmpty()){
                    TreeNode node = s1.pop();
                    if(node != null) {
                        temp.add(node.val);
                        System.out.print("" + node.val);
                        s2.push(node.left);
                        s2.push(node.right);
                    }
                }
                if(!temp.isEmpty()){
                    lists.add(temp);
                    depth++;
                    System.out.println();
                }
            }else{
                ArrayList temp = new ArrayList<>();
                while(!s2.isEmpty()){
                    TreeNode node = s2.pop();
                    if(node != null){
                        temp.add(node.val);
                        System.out.print("" + node.val);
                        s1.push(node.right);
                        s1.push(node.left);
                    }
                }
                if(!temp.isEmpty()){
                    lists.add(temp);
                    depth++;
                    System.out.println();
                }
            }
        }
        return lists;
    }
}

