            return false;
        }
        if(k == str.length-1){
            return true;
        }

        flag[index] = true;
        if(judge(matrix,i+1,j,rows,cols,flag,str,k+1) || judge(matrix,i-1,j,rows,cols,flag,str,k+1)
        || judge(matrix,i,j+1,rows,cols,flag,str,k+1) || judge(matrix,i,j-1,rows,cols,flag,str,k+1)){
